import { HttpClientTestingModule } from '@angular/common/http/testing';
import { TestBed } from '@angular/core/testing';
import { EMPTY } from 'rxjs';
import { GameService } from 'src/app/shared/services/games.service';
import { UserService } from 'src/app/shared/services/user.service';
import { LibraryPageComponent } from './library-page.component';
describe('LibraryPageComponent', () => {
    let component;
    let userService;
    let gameService;
    let games = [
        {
            description: 'description1',
            price: 150,
            tag: ['adventure'],
            title: 'title1',
            id: '-id1',
        },
        {
            description: 'description2',
            price: 100,
            tag: ['adventure'],
            title: 'title2',
            id: '-id2',
        },
        {
            description: 'description3',
            price: 50,
            tag: ['adventure'],
            title: 'title3',
            id: '-id3',
        },
    ];
    beforeEach(async () => {
        await TestBed.configureTestingModule({
            declarations: [LibraryPageComponent],
            imports: [HttpClientTestingModule],
        }).compileComponents();
        const info = {
            age: 50,
            email: 'example@gmail.com',
            friendsList: [],
            gamesList: [],
            username: 'name',
            id: '-id',
        };
        userService = TestBed.inject(UserService);
        gameService = TestBed.inject(GameService);
        component = new LibraryPageComponent(gameService, userService);
        localStorage.setItem('userInfo', JSON.stringify(info));
    });
    it('should create', () => {
        expect(component).toBeTruthy();
    });
    it('should call getAllGames when ngOnInit', () => {
        const spy = spyOn(gameService, 'getAllGames').and.callFake(() => {
            return EMPTY;
        });
        component.ngOnInit();
        expect(spy).toHaveBeenCalled();
    });
    it('should set gamesList to [] if user doesnt have saved games', () => {
        component.ngOnInit();
        expect(component.user.gamesList).toBe([]);
    });
    it('should filter games', () => {
        const info = {
            age: 50,
            email: 'example@gmail.com',
            friendsList: [],
            gamesList: ['-id3'],
            username: 'name',
            id: '-id',
        };
        localStorage.setItem('userInfo', JSON.stringify(info));
        console.log(component.user.gamesList);
        component.filterGames();
        expect(component.gamesList).toBe([games[3]]);
    });
});
