import { HttpClientTestingModule } from '@angular/common/http/testing';
import { TestBed } from '@angular/core/testing';
import { ReactiveFormsModule } from '@angular/forms';
import { RouterTestingModule } from '@angular/router/testing';
import { EMPTY, of } from 'rxjs';
import { UserService } from 'src/app/shared/services/user.service';
import { FriendsPageComponent } from './friends-page.component';
describe('FriendsPageComponent', () => {
    let component;
    let userService;
    let info = {
        age: 50,
        email: 'exmple@gmail.com',
        friendsList: [
            { id: '-MwDErAZp7iK98goW2PU', email: 'abc@gmail.com' },
            { email: 'bcd@gmail.com', id: '-MwDEvoJA0MuEMAwUPLu' },
            { email: 'ijkl@gmail.com', id: '-MwDFYPchUDxdD9rcHze' },
        ],
        gamesList: [{ id: 'id1' }],
        id: '-MwDFeGzNW8gbGRQicfQ',
        username: 'test',
    };
    let allUsers = [
        { email: 'abc@gmail.com', id: '-MwDErAZp7iK98goW2PU' },
        { email: 'bcd@gmail.com', id: '-MwDEvoJA0MuEMAwUPLu' },
        { email: 'cde@gmail.com', id: '-MwDF0cU6bspBxsPJUXM' },
        { email: 'def@gmail.com', id: '-MwDF9GG4fgVTM2xWtUW' },
        { email: 'efg@gmail.com', id: '-MwDFFJt8MQ4WDw1bzOM' },
        { email: 'fgh@gmail.com', id: '-MwDFKUQwOZQzXAZVTiN' },
        { email: 'ghij@gmail.com', id: '-MwDFU4GNO_vFvw26QWp' },
        { email: 'ijkl@gmail.com', id: '-MwDFYPchUDxdD9rcHze' },
        { email: 'steam@gmail.com', id: '-MwMpHEo9TfVPPEPG9zh' },
        {
            age: 50,
            email: 'exmple@gmail.com',
            friendsList: [
                { id: '-MwDErAZp7iK98goW2PU', email: 'abc@gmail.com' },
                { email: 'bcd@gmail.com', id: '-MwDEvoJA0MuEMAwUPLu' },
                { email: 'ijkl@gmail.com', id: '-MwDFYPchUDxdD9rcHze' },
            ],
            gamesList: [{ id: 'id1' }],
            id: '-MwDFeGzNW8gbGRQicfQ',
            username: 'test',
        },
    ];
    let newFriends = [
        { email: 'def@gmail.com', id: '-MwDF9GG4fgVTM2xWtUW' },
        { email: 'efg@gmail.com', id: '-MwDFFJt8MQ4WDw1bzOM' },
        { email: 'fgh@gmail.com', id: '-MwDFKUQwOZQzXAZVTiN' },
        { email: 'ghij@gmail.com', id: '-MwDFU4GNO_vFvw26QWp' },
        { email: 'ijkl@gmail.com', id: '-MwDFYPchUDxdD9rcHze' },
        { email: 'steam@gmail.com', id: '-MwMpHEo9TfVPPEPG9zh' },
    ];
    beforeEach(async () => {
        await TestBed.configureTestingModule({
            declarations: [FriendsPageComponent],
            imports: [
                HttpClientTestingModule,
                RouterTestingModule,
                ReactiveFormsModule,
            ],
        }).compileComponents();
        userService = TestBed.inject(UserService);
        component = new FriendsPageComponent(userService);
        spyOn(userService, 'getCurrentUserInfo').and.callFake(() => {
            return info;
        });
    });
    it('should create', () => {
        expect(component).toBeTruthy();
    });
    it('calls getAllUsers when ngOnInit', () => {
        const spy = spyOn(userService, 'getAllUsers').and.callFake(() => {
            return EMPTY;
        });
        component.ngOnInit();
        expect(spy).toHaveBeenCalled();
    });
    it('filter users, to add someone, who doesn`t in friends', () => {
        spyOn(userService, 'getAllUsers').and.callFake(() => {
            return of(allUsers);
        });
        component.ngOnInit();
        component.filterUsers();
        expect(component.users.length).toEqual(newFriends.length);
    });
});
