import { HttpClientTestingModule } from '@angular/common/http/testing';
import { TestBed } from '@angular/core/testing';
import { ReactiveFormsModule } from '@angular/forms';
import { RouterTestingModule } from '@angular/router/testing';
import { UserService } from 'src/app/shared/services/user.service';
import { GameItemComponent } from './game-item.component';
describe('GameItemComponent', () => {
    let component;
    let userService;
    let games = [
        {
            description: 'description1',
            id: 'id1',
            price: 150,
            tag: ['adventure'],
            title: 'title1',
        },
        {
            description: 'description2',
            id: 'id2',
            price: 50,
            tag: ['indie'],
            title: 'title2',
        },
        {
            description: 'description3',
            id: 'id3',
            price: 100,
            tag: ['action'],
            title: 'title3',
        },
        {
            description: 'description4',
            id: 'id4',
            price: 760,
            tag: ['adventure'],
            title: 'title4',
        },
    ];
    let info = {
        age: 50,
        email: 'exmple@gmail.com',
        gamesList: [],
        id: '-MwDFeGzNW8gbGRQicfQ',
        username: 'test',
    };
    beforeEach(async () => {
        await TestBed.configureTestingModule({
            declarations: [GameItemComponent],
            imports: [
                HttpClientTestingModule,
                RouterTestingModule,
                ReactiveFormsModule,
            ],
        }).compileComponents();
        userService = TestBed.inject(UserService);
        component = new GameItemComponent(userService);
        spyOn(userService, 'getCurrentUserInfo').and.callFake(() => {
            return info;
        });
    });
    it('should create', () => {
        expect(component).toBeTruthy();
    });
    it('should set user`s gameList to [] if user didnt save any games', () => {
        component.ngOnInit();
        expect(component.user.gamesList).toBe([]);
    });
});
